var script_f15_54_16225=[" /* <![CDATA[ */",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function MESS_fx() {",
"with(document.MESS_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1[0].checked){",
"Score = Score + 0;",
"}",
"if (cc1[1].checked){",
"Score = Score + 1;",
"}",
"if (cc1[2].checked){",
"Score = Score + 2;",
"}",
"if (cc2[0].checked){",
"Score = Score + 1;",
"}",
"if (cc2[1].checked){",
"Score = Score + 2;",
"}",
"if (cc2[2].checked){",
"Score = Score + 3;",
"}",
"if (cc2[3].checked){",
"Score = Score + 4;",
"}",
"if (cc3[0].checked){",
"Score = Score + 1;",
"}",
"if (cc3[1].checked){",
"Score = Score + 2;",
"}",
"if (cc3[2].checked){",
"Score = Score + 2;",
"}",
"if (cc3[3].checked){",
"Score = Score + 4;",
"}",
"if (cc3[4].checked){",
"Score = Score + 3;",
"}",
"if (cc3[5].checked){",
"Score = Score + 6;",
"}",
"if (cc4[0].checked){",
"Score = Score + 0;",
"}",
"if (cc4[1].checked){",
"Score = Score + 1;",
"}",
"if (cc4[2].checked){",
"Score = Score + 2;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"",
"rrclr();",
"if ((Score >= 7) && (Score <= 14)){ document.getElementById('rr1_1').bgColor = \"#cccccc\";",
"}",
"if ((Score >= 0) && (Score <= 6)){ document.getElementById('rr1_2').bgColor = \"#cccccc\";",
"}",
"",
"",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"}",
"",
"",
"/* ]]> */"].join("\n");
