"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Indications",
"       </td>",
"       <td class=\"subtitle1\">",
"        Formula description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Age",
"       </td>",
"       <td class=\"subtitle1\">",
"        Examples (listed alphabetically, not comprehensive)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Hyper-metabolism",
"       </td>",
"       <td>",
"        <p>",
"         1-1.5 cal/mL",
"        </p>",
"        <p>",
"         Polymeric",
"        </p>",
"        <p>",
"         May have higher osmolalities",
"        </p>",
"        <p>",
"         May be low-fat",
"        </p>",
"        <p>",
"         May contain MCT",
"        </p>",
"        <p>",
"         May contain increased protein",
"        </p>",
"        <p>",
"         May contain some amino acids thought to be conditionally essential (eg, glutamine, arginine)",
"        </p>",
"        <p>",
"         May contain fish oil",
"        </p>",
"        <p>",
"         Lactose free",
"        </p>",
"        <p>",
"         May contain fiber",
"        </p>",
"       </td>",
"       <td>",
"        &gt;10 years (adults)",
"       </td>",
"       <td>",
"        Impact, Impact with fiber, Impact 1.5, TraumaCal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        <p>",
"         1.0-1.5 cal/mL",
"        </p>",
"        <p>",
"         Hydrolyzed protein source",
"        </p>",
"       </td>",
"       <td>",
"        1-10 years",
"       </td>",
"       <td>",
"        Pepdite Jr, Peptamen Jr, Peptamen Jr 1.5, Vital Jr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &gt;10 years (adults)",
"       </td>",
"       <td>",
