" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Psychosocial and other social factors in acute myocardial infarction",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/55/16250/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/55/16250/contributors\">",
"     Geoffrey H Tofler, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/55/16250/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
