" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Gastrointestinal decontamination of poisoned adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/58/16297/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/58/16297/contributors\">",
"     Robert G Hendrickson, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/58/16297/contributors\">",
"     Shana Kusin, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/58/16297/contributors\">",
"     Section Editor",
