" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Immunotherapy for the prevention and treatment of relapse following hematopoietic cell transplantation",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/58/16298/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/58/16298/contributors\">",
"     Robert S Negrin, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/58/16298/contributors\">",
"     Section Editor",
"    </a>",
