var script_f15_58_16300=[""].join("\n");
var outline_f15_58_16300=null;
var title_f15_58_16301="Specimens for anaerobic culture";
var content_f15_58_16301=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F57141&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F57141&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Specimens appropriate for anaerobic culture",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Specimen and collection method",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normally sterile body fluids",
"       </td>",
"       <td>",
"        Includes blood, pleural fluid, peritoneal fluid, bile, cerebrospinal fluid.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abscess contents",
"       </td>",
"       <td>",
"        Needle aspirates are preferred.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wound*",
"       </td>",
"       <td>",
"        Exudate, preferably collected by syringe aspiration using care to decontaminate the adjacent surface.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Pulmonary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pleural fluid",
"       </td>",
"       <td>",
"        Antecedent antibiotics will reduce yield.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Transtracheal aspirate*",
"       </td>",
"       <td>",
"        The only likely specimen source now is pleural fluid.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Transthoracic needle aspirate",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Thoracotomy specimen",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bronchoscopic aspirate*",
"       </td>",
"       <td>",
"        Requires",
"double-lumen catheter brush with distal occluding plug or",
"bronchoalveolar lavage, each combined with quantitative culture.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tracheostomy aspirate*",
"       </td>",
"       <td>",
"        Validity is not well established; one-third of patients",
"without evidence of infection yield anaerobes, and quantitative",
"cultures may be required.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Urinary tract",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\" rowspan=\"2\">",
"        Suprapubic aspirate",
"       </td>",
"       <td>",
"        Rare source of anaerobic infections.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suspect when positive Gram stain and negative culture.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Female genital tract",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Culdocentesis",
"       </td>",
"       <td>",
"        Experience is varied, seldom done now.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Specimens obtained above pelvic reflection at surgery or laparoscopy",
"       </td>",
"       <td>",
"        Seldom done.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Transabdominal needle aspirates of uterus",
"       </td>",
"       <td>",
"        Seldom done.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intrauterine brush using double catheter with a distal occluding plug*",
"       </td>",
"       <td>",
"        Requires quantitative culture; seldom done.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Intraabdominal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aspirates, biopsy specimens",
"       </td>",
"       <td>",
"        Specimen must be devoid of gastrointestinal flora.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Small bowel aspirate",
"       </td>",
"       <td>",
"        Quantitative culture is necessary to detect bacterial overgrowth syndromes.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Oral-dental",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aspirate of closed spaces",
"       </td>",
"       <td>",
"        Collected from endodontal canal and preferably transported in conditions that preserve hydration and anaerobiosis.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Paper point specimen",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Paranasal sinuses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aspirate using catheter or syringe*",
"       </td>",
"       <td>",
"        Plastic catheter inserted into depths of sinus track is preferred.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Middle-ear aspirate",
"       </td>",
"       <td>",
"        Optimal specimens are obtained with intact tympanic membrane.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\" rowspan=\"1\">",
"        Soft tissue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aspirate of closed spaces, eg, abscesses",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Biopsies using 3-mm dermal punch*",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Because contamination with normal flora is common, broth cultures are inappropriate; interpretation is facilitated with quantitative or semiquantitative culture.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
