var title_f15_62_16366="Erythroderma pathology features";
var content_f15_62_16366=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F87724&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F87724&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histopathologic features of erythroderma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Causes of erythroderma",
"       </td>",
"       <td class=\"subtitle1\">",
"        Key histopathologic features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Idiopathic erythroderma",
"        </strong>",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Nonspecific features including hyperkeratosis, parakeratosis, acanthosis, and inflammatory infiltrate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Inflammatory dermatoses",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Psoriasis",
"       </td>",
"       <td>",
"        Epidermal acanthosis, parakeratosis, Munroe microabscesses, dilated tortuous papillary blood vessels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Atopic dermatitis",
"       </td>",
"       <td>",
"        Acanthosis, hyperkeratosis, spongiosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dermatitis (other than atopic)",
"       </td>",
"       <td>",
"        Acanthosis, hyperkeratosis, spongiosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pityriasis rubra pilaris",
"       </td>",
"       <td>",
"        Acanthosis, hyperkeratosis, alternating ortho and parakeratosis (horizontal and vertical)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lichen planus",
"       </td>",
"       <td>",
"        Lymphohistiocytic infiltrate with band-like distribution upper dermis; obscures the epidermodermal junction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic actinic dermatitis",
"       </td>",
"       <td>",
"        Acanthosis, hyperkeratosis, spongiosis, perivascular chronic inflammatory cell infiltrate, occasional atypical cerebriform lymphoid cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Papuloerythroderma of Ofuji",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sarcoidosis",
"       </td>",
"       <td>",
"        Non-caseating epithelial cell-like granulomas in the deep dermis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Drug reactions",
"        </strong>",
"       </td>",
"       <td class=\"sublist1_start\">",
"        In exanthematous type, interface dermatitis, vacuolar changes, necrotic keratinocytes, inflammatory infiltrate with eosinophils",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Malignancies: hematologic",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cutaneous T-cell lymphoma, Sezary syndrome",
"       </td>",
"       <td>",
"        Findings may be subtle and variable. Sezary cells (highly convoluted cerebriform lymphocytes) along the basal layer, lichenoid infiltrate, epidermotropism, Pautrier microabscesses. Absence of spongiosis.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Other lymphomas (Hodgkin's, non-Hodgkin's)",
"       </td>",
"       <td>",
"        Atypical lymphocytes in dermis and epidermis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Leukemias",
"       </td>",
"       <td>",
"        Leukemic cells in dermis and epidermis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Malignancies: solid organ",
"        </strong>",
"       </td>",
"       <td>",
"        Nonspecific, variable findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Renal cell carcinoma",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatocellular carcinoma",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lung",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Colon",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Immunobullous disease",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pemphigus foliaceus, vulgaris",
"       </td>",
"       <td>",
"        Acantholysis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bullous pemphigoid",
"       </td>",
"       <td>",
"        Subepidermal separation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Paraneoplastic pemphigus",
"       </td>",
"       <td>",
"        Acantholysis, lichenoid infiltrate, necrotic keratinocytes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Connective tissue disease",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dermatomyositis",
"       </td>",
"       <td>",
"        Interface dermatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Subacute lupus erythematosus",
"       </td>",
"       <td>",
"        Interface dermatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Infections",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Scabies",
"       </td>",
"       <td>",
"        Scabies mite at junction of epidermis and stratum corneum; spongiosis, dermal lymphocytic infiltrate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dermatophytosis",
"       </td>",
"       <td>",
"        Hyphae",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Congenital cutaneous candidiasis",
"       </td>",
"       <td>",
"        Hyphae and yeast-like forms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Staphylococcus scalded skin syndrome",
"       </td>",
"       <td>",
"        Vesiculation at level of granular cell layer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Beta-hemolytic streptococcal erythroderma syndrome",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Blood disorders",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hypereosinophilic syndrome",
"       </td>",
"       <td>",
"        Dermal eosinophilic infiltrates",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mastocytosis",
"       </td>",
"       <td>",
"        Dermal mast cell infiltrates",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Graft versus host disease (GVHD)",
"       </td>",
"       <td>",
"        Interface dermatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Erythroderma occurring in the first year of life (congenital/neonatal/infant): additional diagnoses to be considered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Inherited ichthyoses",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Epidermolytic hyperkeratosis (bullous congenital ichthyosiform erythroderma)",
"       </td>",
"       <td>",
"        Hyperkeratosis, acanthosis, superficial vacuolation, clumps of irregular eosinophilic granules granular layer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Non-bullous congenital ichthyosiform erythroderma",
"       </td>",
"       <td>",
"        Acanthosis, hyperkeratosis, focal parakeratosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Netherton syndrome",
"       </td>",
"       <td>",
"        Epidermal psoriasiform hyperplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rare ichthyoses",
"       </td>",
"       <td>",
"        Hyperkeratosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        <strong>",
"         Primary immunodeficiencies",
"        </strong>",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Nonspecific features including hyperkeratosis, parakeratosis, acanthosis, and inflammatory infiltrate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Severe combined immunodeficiency",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Severe combined immunodeficiency - Omenn syndrome (a form of SCID)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        X-linked ectodermal dysplasia with immunodeficiency",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Ectodermal dysplasias",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        AEC syndrome",
"       </td>",
"       <td>",
"        Nonspecific features including hyperkeratosis, parakeratosis, acanthosis, and inflammatory infiltrate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Miscellaneous",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Menke's disease",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
