" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of nontuberculous mycobacteria (excluding MAC) in HIV-infected patients",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/9/15512/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/9/15512/contributors\">",
"     Matthew R Leibowitz, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?15/9/15512/contributors\">",
"     Judith S Currier, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?15/9/15512/contributors\">",
