var title_f16_10_16555="Pediatric RIFLE Classification of AKI";
var content_f16_10_16555=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F68721&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F68721&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pediatric RIFLE Classification of acute kidney injury",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        pRIFLE stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Estimated creatinine clearance (eCCl)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Urine output",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        R = Risk for renal dysfunction",
"       </td>",
"       <td>",
"        eCCl decreased by 25 percent",
"       </td>",
"       <td>",
"        &lt;0.5 mL/kg per hour for 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        I = Injury to the kidney",
"       </td>",
"       <td>",
"        eCCl decreased by 50 percent",
"       </td>",
"       <td>",
"        &lt;0.5 mL/kg per hour for 16 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        F = Failure of kidney function",
"       </td>",
"       <td>",
"        eCCl decreased by 75 percent or eCCl &lt;35 mL/min per 1.73 m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        &lt;0.3 mL/kg per hour for 24 hours or anuria for 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        L = Loss of kidney function",
"       </td>",
"       <td>",
"        Persistent failure &gt;4 weeks",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        E = End-stage renal disease",
"       </td>",
"       <td>",
"        Persistent failure &gt;3 months",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     <p>",
"      &nbsp;",
"     </p>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
