var title_f16_11_16573="Diagnostic eval wheezing";
var content_f16_11_16573=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F62889&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F62889&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Approach to evaluation of wheezing in children based upon suspected diagnosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Suspected diagnosis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Signs and symptoms",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnostic evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Acute",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Asthma",
"       </td>",
"       <td>",
"        History of recurrent wheeze, cough, at least partial response to bronchodilator",
"       </td>",
"       <td>",
"        History, PFT with bronchodilators, empiric trial of bronchodilators, exercise or methacholine challenge testing, chest radiography only if atypical, skin (or",
"        <em>",
"         in vitro",
"        </em>",
"        ) testing for aeroallergen sensitization if history suggests inhalant allergen triggers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Viral bronchiolitis",
"       </td>",
"       <td>",
"        Prodrome with rhinitis, occurs in infancy and early childhood, seasonal pattern",
"       </td>",
"       <td>",
"        History, age, season, rapid antigen testing (RSV, influenza), viral cultures, chest radiography",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Foreign body",
"       </td>",
"       <td>",
"        Sudden onset of coughing and wheezing",
"       </td>",
"       <td>",
"        History, physical examination, chest radiography, bronchoscopy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Chronic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Asthma",
"       </td>",
"       <td>",
"        As above",
"       </td>",
"       <td>",
"        As above",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tracheomalacia",
"       </td>",
"       <td>",
"        Persistent wheeze, starts early in life, poor response to bronchodilators, varies with position and activity",
"       </td>",
"       <td>",
"        History, fluoroscopy, flexible bronchoscopy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cystic fibrosis",
"       </td>",
"       <td>",
"        Chronic productive cough, crackles, with or without clubbing, failure to thrive, recurrent respiratory infections",
"       </td>",
"       <td>",
"        Sweat chloride test, genetic testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Swallowing dysfunction",
"       </td>",
"       <td>",
"        Neurological abnormality (non universal), choking with eating, symptoms exaggerated by feeding",
"       </td>",
"       <td>",
"        Radiographic swallowing study",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Gastroesophageal reflux",
"       </td>",
"       <td>",
"        Symptoms sometimes related to eating, vomiting, refusal to eat, failure to thrive",
"       </td>",
"       <td>",
"        Barium swallow, pH probe, bronchoscopy and bronchoalveolar lavage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vascular ring or sling",
"       </td>",
"       <td>",
"        Persistent symptoms, starts early in infancy, may be exaggerated by position, homophonous wheeze",
"       </td>",
"       <td>",
"        Barium swallow, MRI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tracheal stenosis",
"       </td>",
"       <td>",
"        Persistent symptoms, with or without stridor, homophonous wheeze",
"       </td>",
"       <td>",
"        Chest radiograph, CT scan, bronchoscopy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mediastinal nodes or mass",
"       </td>",
"       <td>",
"        Persistent symptoms, localized wheezing, no response to bronchodilator, systemic symptoms of underlying disease",
"       </td>",
"       <td>",
"        Chest radiograph, CT scan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Immunodeficiency",
"       </td>",
"       <td>",
"        Recurrent sinopulmonary infections, crackles, FTT, clubbing",
"       </td>",
"       <td>",
"        Immunoglobulins, humoral immunity panel",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Primary ciliary dyskinesia",
"       </td>",
"       <td>",
"        Persistent sinusitis and otitis media with draining ears, recurrent respiratory infection, wet cough with sputum production, crackles, clubbing, FTT",
"       </td>",
"       <td>",
"        Ciliary biopsy, with or without genetic testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vocal cord dysfunction",
"       </td>",
"       <td>",
"        Inspiratory stridor, poor response to bronchodilators, absent symptoms during sleep, teenage, exercise related",
"       </td>",
"       <td>",
"        Exercise testing, pulmonary function tests, laryngoscopy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bronchiolitis obliterans",
"       </td>",
"       <td>",
"        History of predisposing disease, ie, viral infection or transplantation, dyspnea, persistent wheezing",
"       </td>",
"       <td>",
"        Chest CT scan, lung biopsy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Dorkin HL. Noisy breathing. In: Respiratory Disease in Children: Diagnosis and Management, Loughlin GM, Eigen H (Eds), Williams and Wilkins 1994. p.171.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
