" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Depression in adults: Supportive psychotherapy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/13/16601/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
