var title_f16_13_16603="Histologic features UIP";
var content_f16_13_16603=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F50587&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F50587&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histologic features of usual interstitial pneumonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Key histologic features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dense fibrosis causing remodeling of lung architecture with frequent \"honycomb\" fibrosis.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fibroblastic foci typically scattered at the edges of dense scars.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patchy lung involvement.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Frequent subpleural and paraseptal distribution.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Pertinent negative findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lack of active lesions of other interstitial diseases (ie, sarcoidosis or Langerhans cell histiocytosis).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lack of marked interstitial chronic inflammation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Granulomas: inconspicuous or absent.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lack of substantial inorganic dust deposits, ie, asbestos bodies (except for carbon black pigment).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lack of marked eosinophilia.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
