"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li>",
"     <a class=\"sr_button\" href=\"#H701771643\" id=\"summRecButton\">",
"      <span>",
"       SUMMARY",
"      </span>",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H1\">",
"      INTRODUCTION",
"     </a>",
