var title_f16_14_16620="Antibiotic rx Legionella";
var content_f16_14_16620=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F65315&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F65315&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antibiotic selection for Legionella infection in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Antimicrobial agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dosing*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Macrolides",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Azithromycin",
"       </td>",
"       <td>",
"        1 g for the first dose, then 500 mg orally or intravenously&bull; every 24 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Clarithromycin",
"       </td>",
"       <td>",
"        500 mg orally or intravenously&Delta; every 12 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Quinolones",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Levofloxacin",
"       </td>",
"       <td>",
"        750 mg orally or intravenously every 24 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Ciprofloxacin",
"       </td>",
"       <td>",
"        400 mg intravenously every 8 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        750 mg orally every 12 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ofloxacin",
"       </td>",
"       <td>",
"        400 mg orally or intravenously every 12 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Moxifloxacin",
"       </td>",
"       <td>",
"        200 mg orally every 24 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Tetracyclines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Doxycycline",
"       </td>",
"       <td>",
"        100 mg orally or intravenously every 12 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Minocycline",
"       </td>",
"       <td>",
"        100 mg orally or intravenously every 12 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tetracycline",
"       </td>",
"       <td>",
"        500 mg orally or intravenously every 6 hr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tigecycline",
"       </td>",
"       <td>",
"        100 mg intravenously for the first dose, then 50 mg every 12 hours thereafter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Trimethoprim-sulfamethoxazole",
"       </td>",
"       <td>",
"        160 and 800 mg intravenously every 8 hr",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Doses based on clinical experience and not on controlled trials.",
"     <br>",
"      &bull; Intravenous form not available in some countries.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
