var title_f16_2_16428="B virus postexposure ppx";
var content_f16_2_16428=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F55170&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F55170&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for postexposure prophylaxis for persons exposed to B virus",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Prophylaxis recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Skin exposure* (with loss of skin integrity) or mucosal exposure (with or without injury) to a high-risk source (eg, a macaque that is ill, immunocompromised, or known to be shedding virus or that has lesions compatible with B virus disease)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inadequately cleaned skin exposure (with loss of skin integrity) or mucosal exposure (with or without injury)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laceration of the head, neck, or torso",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Deep puncture bite",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Needlestick associated with tissue or fluid from the nervous system, lesions suspicious for B virus, eyelids, or mucosa",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Puncture or laceration after exposure to objects (a) contaminated either with fluid from monkey oral or genital lesions or with nervous system tissues, or (b) known to contain B virus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A postcleansing culture is positive for B virus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Prophylaxis considered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mucosal splash that has been adequately cleaned",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Laceration (with loss of skin integrity) that has been adequately cleaned",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Needlestick involving blood from an ill or immunocompromised macaque",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Puncture or laceration occurring after exposure to (a) objects contaminated with body fluid (other than that from a lesion), or (b) potentially infected cell culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Prophylaxis not recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Skin exposure in which the skin remains intact",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Exposure associated with nonmacaque species of nonhuman primates",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Exposures include macaque bites; macaque scratches; or contact with ocular, oral, or genital secretions, nervous system tissue, or material contaminated by macaques (eg, cages or equipment).",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Cohen, JI, Davenport, DS, Stewart, JA, et al. Recommendations for Prevention of and Therapy for Exposure to B Virus (Cercopithecine Herpesvirus 1). Clin Infect Dis 2002; 35:1191. Copyright &copy; 2002 University of Chicago Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
