" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Skin examination and clinical features of melanoma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
