var title_f16_20_16715="Lab practices to avoid Brucella exposure";
var content_f16_20_16715=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F78971&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F78971&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for safe laboratory practices to avoid exposure to",
"    <em>",
"     Brucella",
"    </em>",
"    spp.",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       When brucellosis is suspected, clinicians or forwarding laboratories should note on the laboratory submission: \"Suspect or rule out brucellosis.\"",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Review laboratory containment methods and microbiologic procedures to ensure compliance with recommendations in the",
"       <em>",
"        Biosafety in Microbiological and Biomedical Laboratories, Fifth Edition",
"       </em>",
"       .",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Use primary barriers (ie, safety centrifuge cups, personal protective equipment, and Class II or higher biological safety cabinets [BSCs]) for procedures with a high likelihood of producing droplet splashes or aerosols.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Use secondary barriers: restrict access to the laboratory when work is being performed and maintain the integrity of the laboratory air-handling system by keeping external doors and windows closed.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Avoid causing splashes or aerosols when performing procedures on unidentified isolates.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Prohibit sniffing of open culture plates to assist in the identification of isolates.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Manipulate isolates of small gram-negative or gram-variable rods initially inside a BSC.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: MMWR Surveill Summ 2008; 57:39.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
