var title_f16_20_16718="Pulmonary complications of allogeneic HCT - Postengraftment";
var content_f16_20_16718=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F83195&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F83195&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pulmonary complications of allogeneic hematopoietic cell transplantation: Postengraftment",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disease process",
"       </td>",
"       <td class=\"subtitle1\">",
"        Risk factors",
"       </td>",
"       <td class=\"subtitle1\">",
"        Associated manifestations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Radiographic findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Useful diagnostic tests",
"       </td>",
"       <td class=\"subtitle1\">",
"        Lung biopsy needed",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Bacterial pneumonia",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Focal or patchy consolidation, may be peribronchial",
"        </p>",
"        <p>",
"         Occasionally mass-like \"round-pneumonia\"",
"        </p>",
"       </td>",
"       <td>",
"        Legionella urinary antigen, blood and BAL cultures, special stains/cultures for Actinomycosis and Nocardia",
"       </td>",
"       <td>",
"        Rarely",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Mycobacterial pneumonia",
"       </td>",
"       <td>",
"        Total body irradiation, chronic GVHD",
"       </td>",
"       <td>",
"        M. haemophilum is associated with skin nodules and/or joint inflammation",
"       </td>",
"       <td>",
"        Miliary pattern",
"       </td>",
"       <td>",
"        PPD after HCT not helpful; cultures of sputum and BAL are helpful",
"       </td>",
"       <td>",
"        Rarely",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        CMV pneumonitis",
"       </td>",
"       <td>",
"        Seropositive recipient with seronegative donor; delayed reconstitution, prior treatment for CMV",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        CT: patchy or diffuse ground-glass opacities, patchy consolidation, small nodular opacities; rarely tree-in-bud pattern",
"       </td>",
"       <td>",
"        Serology, blood test for pp65 antigen or CMV PCR, BAL and endobronchial brush&nbsp;for inclusion bodies and BAL cultures for CMV",
"       </td>",
"       <td>",
"        Rarely",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Respiratory viruses",
"       </td>",
"       <td>",
"        Exposure to someone with active viral infection",
"       </td>",
"       <td>",
"        URI symptoms prior to onset of lower respiratory tract symptoms",
"       </td>",
"       <td>",
"        Diffuse ground glass opacities are the most common",
"       </td>",
"       <td>",
"        Culture or rapid immunofluorescence of nasopharyngeal lavage or swab and BAL fluid",
"       </td>",
"       <td>",
"        Sometimes to completely exclude other possibilities",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Fungal infection (eg, invasive aspergillosis, Fusarium, Zygomycetes, Candida, Scedosporium, Pneumocystis)",
"       </td>",
"       <td>",
"        Presence and severity GVHD, older patient age, cytopenia, CMV infection",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Focal nodular and consolidative opacities, \"halo sign\", sometimes subpleural wedge-shaped densities",
"       </td>",
"       <td>",
"        Broad based cultures of blood and BAL; blood tests for &beta;-D-glucan and Aspergillus galactomannan and BAL for galactomannan",
"       </td>",
"       <td>",
"        Sometimes when cultures are negative and no response to initial therapy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Idiopathic pneumonia syndrome",
"       </td>",
"       <td>",
"        Busulfan, high dose cyclophosphamide, radiation, nonmyeloablative conditioning regimen",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Extensive opacities",
"       </td>",
"       <td>",
"        Negative blood and BAL cultures",
"       </td>",
"       <td>",
"        Yes, either transbronchial or surgical",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Diffuse alveolar hemorrhage",
"       </td>",
"       <td>",
"        Underlying mucopolysaccharidosis",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        CT: patchy or diffuse opacities with air bronchograms",
"       </td>",
"       <td>",
"        BAL showing increasingly bloody return in sequential lavages and &gt;20 percent hemosiderin-laden macrophages",
"       </td>",
"       <td>",
"        Not usually",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Connective tissue disease",
"       </td>",
"       <td>",
"        Myeloablative conditioning regimen",
"       </td>",
"       <td>",
"        Extrapulmonary manifestations such as dry mouth/dry eyes, joint pain/swelling, muscle weakness",
"       </td>",
"       <td>",
"        CT: subpleural, ground-glass opacities; septal thickening",
"       </td>",
"       <td>",
"        Autoantibody tests positive",
"       </td>",
"       <td>",
"        Often to identify specific type of interstitial pneumonitis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Organizing pneumonia",
"       </td>",
"       <td>",
"        Irradiation, CMV infection, HCT associated connective tissue disease, chronic GVHD",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        CT: patchy air-space consolidation, ground-glass opacities, small nodular opacities",
"       </td>",
"       <td>",
"        Lung biopsy",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Malignancy",
"       </td>",
"       <td>",
"        Underlying lymphoma, EBV infection in posttransplant lymphoproliferative disorder",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Nodular opacities, lymphangitic pattern",
"       </td>",
"       <td>",
"        BAL cytology and flow cytometry, biopsy",
"       </td>",
"       <td>",
"        Biopsy usually needed",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Pulmonary alveolar proteinosis",
"       </td>",
"       <td>",
"        HCT for myeloid disorder",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Perihilar opacities in a \"bat-wing\" distribution often with air bronchograms",
"       </td>",
"       <td>",
"        Bronchoalveolar lavage showing characteristic milky appearance and positive stain for lipoproteins",
"       </td>",
"       <td>",
"        Not usually",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Pulmonary cytolytic thrombi",
"       </td>",
"       <td>",
"        Chronic GVHD is a risk factor",
"       </td>",
"       <td>",
"        Low grade fever, cough",
"       </td>",
"       <td>",
"        CT: peripheral nodules",
"       </td>",
"       <td>",
"        BAL to rule out infection; lung biopsy",
"       </td>",
"       <td>",
"        Yes, findings are basophilic cytolytic thrombi in the small to medium distal pulmonary vessels with entrapped monocytes",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Pulmonary veno-occlusive disease",
"       </td>",
"       <td>",
"        Onset after first 100 days, chronic GVHD",
"       </td>",
"       <td>",
"        Reduced DLCO",
"       </td>",
"       <td>",
"        CXR: pleural effusion and Kerley B lines; CT chest: centrilobular ground glass opacities; no emboli on CTPA",
"       </td>",
"       <td>",
"        Right heart catheterization; BAL showing occult hemorrhage",
"       </td>",
"       <td>",
"        For definitive diagnosis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Drug toxicity",
"       </td>",
"       <td>",
"        History of pneumotoxic drug use (eg, busulfan, cyclophosphamide)",
"       </td>",
"       <td>",
"        May be associated with rash, peripheral eosinophilia",
"       </td>",
"       <td>",
"        Varied",
"       </td>",
"       <td>",
"        Increased BAL eosinophils may be seen; other processes excluded by negative blood and BAL stains and cultures, negative fungal studies",
"       </td>",
"       <td>",
"        Sometimes to completely exclude other possibilities",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Radiation pneumonitis",
"       </td>",
"       <td>",
"        History of radiation therapy involving lungs",
"       </td>",
"       <td>",
"        <p>",
"         Acute: onset usually 4 to 12 weeks following irradiation",
"        </p>",
"        <p>",
"         Late: onset after 6 to 12 months",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Acute CT: ground-glass attenuation within the area of irradiated lung",
"        </p>",
"        <p>",
"         Late CT: linear opacities (scarring) or an area of dense consolidation and volume loss",
"        </p>",
"       </td>",
"       <td>",
"        Other processes excluded by negative blood and BAL stains and cultures, negative fungal studies",
"       </td>",
"       <td>",
"        Sometimes to completely exclude other possibilities",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Bronchiolitis obliterans",
"       </td>",
"       <td>",
"        Chronic GVHD, postviral",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Initially clear, as progresses, may have mosaic ground glass opacities on CT and bronchiectasis",
"       </td>",
"       <td>",
"        <p>",
"         Spirometry",
"        </p>",
"        <p>",
"         Skin biopsy for GVHD",
"        </p>",
"       </td>",
"       <td>",
"        Sometimes if diagnosis uncertain",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BAL: bronchoalveolar lavage; GVHD: graft-versus-host disease; PPD: purified protein derivative skin test; HCT: hematopoietic cell transplantation; CMV: cytomegalovirus; PCR: polymerase chain reaction; URI: upper respiratory infection; CT: computed tomography; EBV: Epstein-Barr virus; DLCO: diffusing capacity for carbon monoxide; CXR: chest radiograph.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
