" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Surgical oncologic principles for the resection of colon cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/23/16762/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/23/16762/contributors\">",
"     Miguel A Rodriguez-Bigas, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/23/16762/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/23/16762/contributors\">",
"     Kenneth K Tanabe, MD",
"    </a>",
"    <br/>",
"   </div>",
