var title_f16_24_16780="Issues anticoag for malignancy";
var content_f16_24_16780=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F81646&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F81646&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Issues to be considered when making decisions concerning the use or non-use of anticoagulation in patients with malignancy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Overall treatment plan and prognosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Therapeutic goal (eg, palliation of symptoms related to VTE, planned oncologic intervention, immediate prognosis, Hospice care)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Planned chemotherapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage of the malignancy, overall prognosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Other causes for hypercoagulability (eg, bed ridden, pathologic fracture, recent surgery or invasive procedures, use of hormonal agents or other medications with a high incidence of thrombotic side-effects, presence of a central venous access line)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patient preferences, logistic, and financial issues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inpatient or outpatient care planned",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Who will supervise medication's use and required monitoring, if any?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patient preferences (eg, oral versus injectable agent, need for frequent monitoring of coagulation status)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Food and Drug Administration approval of agent chosen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Treatment costs. Will the patient's insurance pay for the medication?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Relative contraindications to anticoagulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Presence or absence of central nervous system involvement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Central nervous system primary or metastases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Prior intracerebral hemorrhage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thrombocytopenia or bleeding diathesis present?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are risk factors for bleeding after use of warfarin present? (eg, impaired liver function, hepatic metastases, concomitant medication, poor nutrition)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Technical issues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Selection of appropriate dosing (eg, impaired renal function, obesity, advanced age)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Need for monitoring of treatment (eg, INR for treatment with warfarin)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ability or inability to reverse anticoagulation if bleeding occurs",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
