var title_f16_3_16444="Dose modif neurotox oxaliplatin";
var content_f16_3_16444=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F57185&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F57185&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Dose modifications for neurotoxicity in patients receiving oxaliplatin 85 mg/m",
"    <sup>",
"     2",
"    </sup>",
"    every two weeks*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type/duration of neurotoxicity",
"       </td>",
"       <td class=\"subtitle1\">",
"        Transient (&lt;7 days)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Nonpersistent (&gt;7 days but &lt;14 days)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Persistent between courses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cold-related dysesthesia",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paresthesias (mild)",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Paresthesias associated with pain or complaint",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        None",
"       </td>",
"       <td>",
"        Normal examination: reduce to 75 mg/m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        Stop until improvement/recovery.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abnormal examination: omit one cycle oxaliplatin, then restart at 75 mg/m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        If improvement/recovery, restart at 75 mg/m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Paresthesias associated with functional impairment",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        None",
"       </td>",
"       <td>",
"        Normal examination and not interfering with activities of daily living: reduce to 75 mg/m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        Stop until improvement/recovery.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abnormal examination or interfering with activities of daily living: omit one cycle oxaliplatin, then restart at 75 mg/m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"       <td>",
"        If improvement/recovery, restart at 75 mg/m",
"        <sup>",
"         2",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * For patients on oxaliplatin 130 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     every three weeks, the dose should be reduced to 100 mg/m",
"     <sup>",
"      2",
"     </sup>",
"     .",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Gamelin, E, Gamelin, L, Bossi, L, Quasthoff. Clinical aspects and molecular basis of oxaliplatin neurotoxicity: Current management and development of preventive measures. Semin Oncol 2002: (suppl 15) 29:25.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
