var title_f16_33_16925="Neurologic complications of cardiac surgery";
var content_f16_33_16925=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F68206&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F68206&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Neurologic complications of cardiac operations",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Complication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Incidence",
"       </td>",
"       <td class=\"subtitle1\">",
"        Preventive measures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Brachial plexus",
"injury",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        2-15%",
"       </td>",
"       <td>",
"        Minimize sternal retraction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Attention to patient position",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Padding arms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"17\">",
"        Stroke",
"       </td>",
"       <td colspan=\"1\" rowspan=\"17\">",
"        0.4-6%",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Conduct of cardiopulmonary bypass",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Minimize duration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypothermia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Alpha-stat pH management (?)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Minimally invasive procedures (?)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Avoid perioperative hypotension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Preoperative screening",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        If significant carotid stenosis",
"consider:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Preoperative stent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Combined CEA/CABG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Avoidance of emboli",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        De-airing maneuvers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Minimize aortic trauma in",
"patients with severely calcified aortas",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Monitoring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        TEE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        EEG/EP (?)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        TCD (?)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Ophthalmologic",
"       </td>",
"       <td>",
"        Clinical 0.1-25%",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Same as stroke",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Angiography 100%",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"9\">",
"        Delirium",
"       </td>",
"       <td colspan=\"1\" rowspan=\"9\">",
"        3-32%",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Minimize:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Sensory deprivation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Benzodiazepine/narcotic use",
"postoperatively",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Disruption of sleep-wake cycles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Watch for metabolic encephalopathies;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Electrolytes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Renal dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hepatic dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Watch for withdrawal syndromes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Subtle neuropsychological",
"       </td>",
"       <td>",
"        10-79%",
"       </td>",
"       <td>",
"        Same as stroke",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intracranial hemorrhage",
"       </td>",
"       <td>",
"        &lt;0.1%",
"       </td>",
"       <td>",
"        Wait 2-4 weeks after new stroke before surgery if",
"possible",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"9\">",
"        Seizures",
"       </td>",
"       <td colspan=\"1\" rowspan=\"9\">",
"        0.6%",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Avoid:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hyponatremia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypocalcemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypomagnesemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Avoid withdrawal of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Benzodiazepines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Barbiturates",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Avoid toxic doses of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Lidocaine",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CEA: carotid endarterectomy; EEG: electroencephalography; CABG: coronary artery bypass grafting; EP: evoked potentials; TEE: transesophageal echocardiography; TCD: transcranial Doppler.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
