var title_f16_34_16940="Neonatal pain assess tools";
var content_f16_34_16940=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F80078&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F80078&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Commonly used measures of pain in neonates",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Measure",
"       </td>",
"       <td class=\"subtitle1\">",
"        Variables included",
"       </td>",
"       <td class=\"subtitle1\">",
"        Type of pain",
"       </td>",
"       <td class=\"subtitle1\">",
"        Psychometric testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PIPP (Premature Infant Pain Profile)",
"       </td>",
"       <td>",
"        Heart rate, oxygen saturation, facial actions; takes state and gestational age into account",
"       </td>",
"       <td>",
"        Procedural, postoperative (minor)",
"       </td>",
"       <td>",
"        Reliability, validity, clinical utility well established",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NIPS (Neonatal Infant Pain Score)",
"       </td>",
"       <td>",
"        Facial expression, crying, breathing patterns, arm and leg movements, arousal",
"       </td>",
"       <td>",
"        Procedural",
"       </td>",
"       <td>",
"        Reliability, validity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NFCS (Neonatal Facial Coding System)",
"       </td>",
"       <td>",
"        Facial actions",
"       </td>",
"       <td>",
"        Procedural",
"       </td>",
"       <td>",
"        Reliability, validity, clinical utility, high degree of sensitivity to analgesia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        N-PASS (Neonatal Pain, Agitation, and Sedation Scale)",
"       </td>",
"       <td>",
"        Crying, irritability, behavioral state, facial expression, extremity tone, vital signs",
"       </td>",
"       <td>",
"        Postoperative, procedural, ventilated",
"       </td>",
"       <td>",
"        Reliability, validity, includes sedation end of scale, does not distinguish pain from agitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CRIES (Cry, Requires oxygen, Increased vital signs, Expression, Sleeplessness)",
"       </td>",
"       <td>",
"        Crying, facial expression, sleeplessness, requires oxygen to stay at &gt;95 percent saturation, increased vital signs",
"       </td>",
"       <td>",
"        Postoperative",
"       </td>",
"       <td>",
"        Reliability, validity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        COMFORT Scale",
"       </td>",
"       <td>",
"        Movement, calmness, facial tension, alertness, respiration rate, muscle tone, heart rate, blood pressure",
"       </td>",
"       <td>",
"        Postoperative, critical care, developed for sedation, recently validated for postoperative pain in 0- to 3-year-old infants",
"       </td>",
"       <td>",
"        Reliability, validity, clinical utility",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Anand KJ, Aranda JV, Berde CB, et al. Summary proceedings from the neonatal pain-control group. Pediatrics 2006; 117:S9. Copyright &copy; 2006 American Academy of Pediatrics.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
