"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"        Post-transplant period",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"3\" rowspan=\"1\">",
"        Relative risk (95 percent CI) PD:HD",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Graft failure",
"       </td>",
"       <td class=\"subtitle2\">",
"        Death-censored graft failure",
"       </td>",
"       <td class=\"subtitle2\">",
"        Death",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        During the first 3 months",
"       </td>",
"       <td>",
"        1.23 (1.09-1.39)*",
"       </td>",
