var title_f16_36_16972="Clues RD early childhood";
var content_f16_36_16972=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F69787&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F69787&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clues to dyslexia in early childhood",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       The preschool years",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Trouble learning common nursery rhymes, such as \"Jack and Jill\" and \"Humpty Dumpty\"",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       A lack of appreciation of rhymes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Mispronounced words; persistent baby talk",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Difficulty in learning (and remembering) names of letters",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Failure to know the letters in his own name",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Kindergarten and first grade",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Failure to understand that words come apart; for example, that",
"       <em>",
"        batboy",
"       </em>",
"       can be pulled apart into",
"       <em>",
"        bat",
"       </em>",
"       and",
"       <em>",
"        boy",
"       </em>",
"       , and, later on, that the word",
"       <em>",
"        bat",
"       </em>",
"       can be broken down still further and sounded out as: \"",
"       <em>",
"        b",
"       </em>",
"       \" \"",
"       <em>",
"        aaa",
"       </em>",
"       \" \"",
"       <em>",
"        t",
"       </em>",
"       \"",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Inability to learn to associate letters with sounds, such as being unable to connect the letter",
"       <em>",
"        b",
"       </em>",
"       with the \"",
"       <em>",
"        b",
"       </em>",
"       \" sound",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Reading errors that show no connection to the sounds of the letters; for example, the word",
"       <em>",
"        big",
"       </em>",
"       is read as",
"       <em>",
"        goat",
"       </em>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       The inability to read common one-syllable words or to sound out even the simplest of words such as",
"       <em>",
"        mat",
"       </em>",
"       ,",
"       <em>",
"        cat",
"       </em>",
"       ,",
"       <em>",
"        hop",
"       </em>",
"       ,",
"       <em>",
"        nap",
"       </em>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Complaints about how hard reading is, or running and hiding when it is time to read",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       A history of reading problems in parents or siblings",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Shaywitz S. Overcoming Dyslexia: A New and Complete Science-Based Program for Reading Problems at Any Level. Copyright &copy; 2003 by Sally Shaywitz, M.D. Used with permission of Alfred A. Knopf, a division of Random House, Inc. For on-line information about other Random House, Inc. books and authors, see the Internet Web site at www.randomhouse.com.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
