var title_f16_38_17004="DDx Foot ankle pain athlete B";
var content_f16_38_17004=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F82706&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F82706&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of foot and ankle pain in the young athlete (continued)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of injury",
"       </td>",
"       <td class=\"subtitle1\">",
"        Location of pain",
"       </td>",
"       <td class=\"subtitle1\">",
"        Radiograph",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Medial",
"ankle pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Flexor hallucis longus injuries",
"       </td>",
"       <td>",
"        Extreme plantar flexion",
"       </td>",
"       <td>",
"        FHL tendon (behind the medial malleolus); may radiate",
"to the medial arch",
"       </td>",
"       <td>",
"        AP, lateral, and mortise views of the ankle; lateral",
"ankle in full equinus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Flexor digitorum longus injuries",
"       </td>",
"       <td>",
"        Excessive pronation",
"       </td>",
"       <td rowspan=\"2\">",
"        Medial plantar surface of FDL tendon",
"       </td>",
"       <td rowspan=\"2\">",
"        Not necessary for diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased effort of plantar flexion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"3\">",
"        Os tibiale externum",
"       </td>",
"       <td>",
"        Direct pressure",
"       </td>",
"       <td rowspan=\"3\">",
"        Navicular tuberosity",
"       </td>",
"       <td rowspan=\"3\">",
"        AP, lateral, and oblique views of the foot",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Direct trauma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eversion ankle injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tibialis posterior tendon dysfunction",
"       </td>",
"       <td>",
"        Increased stress along TPT (eg, dancers, skaters,",
"running sports)",
"       </td>",
"       <td>",
"        Along TPT, usually distal to the medial malleolus",
"       </td>",
"       <td>",
"        AP, lateral foot; MRI depending upon stage of disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        S1 radiculopathy",
"       </td>",
"       <td>",
"        Disc herniation",
"       </td>",
"       <td>",
"        Posterior leg and foot",
"       </td>",
"       <td>",
"        Consider MRI spine (after EMG/NCS)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tarsal tunnel syndrome",
"       </td>",
"       <td>",
"        S/P fracture or dislocation of talus, calcaneus, or",
"medial malleolus",
"       </td>",
"       <td>",
"        Plantar foot; distal foot toes; heel",
"       </td>",
"       <td>",
"        Consider MRI ankle (after EMG/NCS)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     FHL: flexor hallucis longus; AP: anteroposterior; FDL: flexor digitorum longus; TPT: tibialis posterior tendon; MRI: magnetic resonance imaging; EMG: electomyography; NCS: nerve conduction studies.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
