var title_f16_39_17019="Muscle strength test";
var content_f16_39_17019=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F66888&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F66888&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Muscle strength grading scale*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Grade",
"      </td>",
"      <td class=\"subtitle1\">",
"       Demonstrated strength",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5",
"      </td>",
"      <td>",
"       Muscle contracts normally against full resistance.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4",
"      </td>",
"      <td>",
"       Muscle strength is reduced but muscle contraction can still move joint against resistance.&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3",
"      </td>",
"      <td>",
"       Muscle strength is further reduced such that the joint can be moved only against gravity with the examiner's resistance completely removed.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2",
"      </td>",
"      <td>",
"       Muscle can move only if the resistance of gravity is removed (eg, in the horizontal plane).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1",
"      </td>",
"      <td>",
"       Only a trace of movement is seen or felt or fasciculations are present.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       0",
"      </td>",
"      <td>",
"       No movement.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * This scale relies on full patient effort and cooperation. Thus, patients who are unable to follow instructions due to young age, cognitive deficits, or decreased level of consciousness cannot be tested in this manner.",
"     <br>",
"      &bull; This system is relatively less sensitive for the evaluation of mild weakness. To compensate for this, the addition of 4+ and 4- categories are sometimes added to the scale.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
