" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Epidural abscess",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/40/17033/contributors\">",
