" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pulmonary outcomes of bronchopulmonary dysplasia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/40/17034/contributors\">",
