" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Diagnosis and treatment of disorders of the androgen receptor",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/44/17096/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/44/17096/contributors\">",
