" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Medication overuse headache: Treatment and prognosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/48/17161/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/48/17161/contributors\">",
"     Ivan Garza, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/48/17161/contributors\">",
"     Todd J Schwedt, MD, MSCI",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/48/17161/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/48/17161/contributors\">",
"     Jerry W Swanson, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/48/17161/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/48/17161/contributors\">",
"     John F Dashe, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?16/48/17161/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
"   <span>",
