var title_f16_48_17165="Tdap Td contraindications";
var content_f16_48_17165=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F79169&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F79169&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Contraindications and precautions to diphtheria, tetanus, and pertussis vaccines for children and adolescents aged 7 through 18 years",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Vaccine",
"       </td>",
"       <td class=\"subtitle1\">",
"        Contraindications",
"       </td>",
"       <td class=\"subtitle1\">",
"        Precautions*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Neither contraindications nor precautions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Tdap",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"7\">",
"        &nbsp;",
"       </td>",
"       <td>",
"        Severe allergic reaction (eg, anaphylaxis) after a previous vaccine dose or to a vaccine component",
"       </td>",
"       <td>",
"        Moderate or severe acute illness with or without fever",
"       </td>",
"       <td>",
"        History of extensive limb-swelling reaction after a previous dose of DTwP/DTaP or Td immunization that was not an Arthus-type hypersensitivity reaction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Encephalopathy (eg, coma, decreased level of consciousness, prolonged seizures) not attributable to another identifiable cause within 7 days of administration of previous doses of DTwP/DTaP/Tdap",
"       </td>",
"       <td>",
"        Guillain-Barr&eacute; syndrome within 6 weeks of tetanus toxoid-containing vaccine",
"       </td>",
"       <td>",
"        Stable&nbsp;neurologic disorder (eg, well-controlled seizures, history of seizure disorder, cerebral palsy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Progressive or unstable neurologic disorder, uncontrolled seizures or progressive encephalopathy until a treatment regimen has been established and the condition stabilized",
"       </td>",
"       <td>",
"        Brachial neuritis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        History of Arthus-type hypersensitivity reaction following diphtheria or tetanus toxoid-containing vaccine (including MCV4)",
"       </td>",
"       <td>",
"        Non-anaphylactic latex allergy",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Breastfeeding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Immunosuppression, including HIV infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Use of antimicrobials",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Tetanus toxoid, reduced diphtheria toxoid (Td, adult Td, dT)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        &nbsp;",
"       </td>",
"       <td>",
"        Severe allergic reaction (eg, anaphylaxis) after a previous vaccine dose or to a vaccine component",
"       </td>",
"       <td>",
"        Moderate or severe acute illness with or without fever",
"       </td>",
"       <td>",
"        (As for Tdap)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Guillain-Barr&eacute; syndrome within 6 weeks of tetanus toxoid-containing vaccine",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        History of Arthus-type hypersensitivity reaction following diphtheria or tetanus toxoid-containing vaccine (including MCV4)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     Tdap: tetanus toxoid, reduced diphtheria toxoid, acellular pertussis vaccine; DTaP: diphtheria toxoid, tetanus toxoid, acellular pertussis vaccine; DTwP: diphtheria tetanus, whole-cell pertussis vaccine; Td: tetanus toxoid, reduced diphtheria toxoid (adult Td, dT); MCV4: quadrivalent meningococcal vaccine; HIV: human immunodeficiency virus.",
"     <br>",
"      * Precautions should be reviewed carefully. Benefits and risks of administering specific vaccines to a person under these circumstances should be considered. If the risk from the vaccine is believed to outweigh the benefit, the vaccine should not be administered.",
"      <br>",
"       &bull;",
"       <font color=\"black\">",
"        Vaccines that contain latex (eg, in the tip or rubber plunger) are contraindicated in patients with anaphylactic reactions to latex. Check the prescribing information for information about latex.",
"       </font>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from:",
"     <br>",
"      <ol>",
"       <li>",
"        Immunization Action Coalition. Guide to contraindications and precautions to commonly used vaccines. Available at:",
"        <a href=\"file://www.immunize.org/catg.d/p3072.pdf\">",
"         www.immunize.org/catg.d/p3072.pdf",
"        </a>",
"        . Accessed on June 23, 2012.",
"       </li>",
"       <li>",
"        Kroger AT, Sumaya CV, Pickering LK, Atkinson WL. General recommendations on immunization: recommendations of the Advisory Committee on Immunization Practices (ACIP). MMWR Recomm Rep 2011; 60:1.&nbsp;",
"       </li>",
"       <li>",
"        American Academy of Pediatrics. Pertussis (whooping cough). In: Red Book: 2012 Report of the Committee on Infectious Diseases, 29th, Pickering LK. (Ed), American Academy of Pediatrics, Elk Grove Village, IL 2012. p.553.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
