var title_f16_5_16477="Tissue stains for endoscopy";
var content_f16_5_16477=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F68885&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F68885&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Tissue stains used during gastrointestinal endoscopy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stain type",
"       </td>",
"       <td class=\"subtitle1\">",
"        What is stained",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of staining",
"       </td>",
"       <td class=\"subtitle1\">",
"        Positive staining",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical uses in GI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Vital stains",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"3\">",
"        Lugol's solution (iodine + potassium iodide)",
"       </td>",
"       <td rowspan=\"3\">",
"        Normal glycogen containing squamous cells",
"       </td>",
"       <td rowspan=\"3\">",
"        Binds iodine in non-keratinized cells",
"       </td>",
"       <td rowspan=\"3\">",
"        Dark brown",
"       </td>",
"       <td>",
"        1) Squamous cell esophageal cancer (non-staining)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2) Columnar epithelium in the esophagus, including residual Barrett's esophagus following mucosal ablation (non-staining)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3) Reflux esophagitis (non-staining)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"5\">",
"        Methylene blue (methylthionine chloride)",
"       </td>",
"       <td rowspan=\"5\">",
"        Small or large intestinal cells or intestinal metaplasia",
"       </td>",
"       <td rowspan=\"5\">",
"        Active absorption into cells",
"       </td>",
"       <td rowspan=\"5\">",
"        Blue",
"       </td>",
"       <td>",
"        1) Specialized epithelium (intestinal metaplasia) in Barrett's esophagus*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2) Intestinal metaplasia in the stomach",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3) Early gastric cancer&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4) Gastric metaplasia in the duodenum (non-staining)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5) Celiac and tropical sprue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Toluidine blue (tolonium chloride or dimethylamino-toluphenazothioni-chloride)",
"       </td>",
"       <td rowspan=\"2\">",
"        Nuclei of columnar (gastric and intestinal-type) cells",
"       </td>",
"       <td rowspan=\"2\">",
"        Diffuses into cell",
"       </td>",
"       <td rowspan=\"2\">",
"        Blue",
"       </td>",
"       <td>",
"        1) Squamous cell carcinoma of the esophagus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2) Gastric or intestinal metaplasia in Barrett's esophagus",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Reactive stains",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Congo red (biphenylene-napthadene sulfornic acid)",
"       </td>",
"       <td rowspan=\"2\">",
"        Acid-containing gastric cells",
"       </td>",
"       <td rowspan=\"2\">",
"        Acid pH &lt;3.0 results in color change",
"       </td>",
"       <td rowspan=\"2\">",
"        Turns red to dark blue or black",
"       </td>",
"       <td>",
"        1) Acid-secreting gastric mucosa (including ectopic locations)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2) Gastric cancer (nonstaining); (may be combined with methylene blue to outline intestinal metaplasia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Phenol red (phenolsulfonphthalein)",
"       </td>",
"       <td>",
"        H. pylori-infected gastric cells",
"       </td>",
"       <td>",
"        Alkaline pH (from hydrolysis of urea to NH3 and CO2 by urease) results in color change",
"       </td>",
"       <td>",
"        Turns yellow to red",
"       </td>",
"       <td>",
"        Diagnose Helicobacter pylori infection (positive color change) and map its distribution in the stomach",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Contrast stain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Indigo carmine&Delta;",
"       </td>",
"       <td rowspan=\"2\">",
"        Cells are not stained",
"       </td>",
"       <td rowspan=\"2\">",
"        Pools in crevices and valleys between mucosal projections",
"       </td>",
"       <td rowspan=\"2\">",
"        Blue (indigo)",
"       </td>",
"       <td>",
"        1) Colon, gastric, duodenal, esophageal lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2) Barrett's esophagus",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Methylene blue does not stain non-specialized or gastric metaplasia; specialized columnar epithelium stains blue, but highly dysplastic or malignant specialized columnar epithelium in Barrett's esophagus generally takes up little to no dye; low grade dysplasia in Barrett's esophagus may or may not take up stain.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      With or without Congo red.",
"      <br>",
"       &Delta; Also used in combination with high resolution or high magnification endoscopy; may be used with or without crystal violet (for early colorectal cancers).",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Canto M. Staining in Gastrointestinal Endoscopy: The Basics. Endoscopy 1999; 31:479. Copyright &copy; 1999 Thieme Medical Publishers, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
