var title_f16_53_17243="Psychosomatic abdominal pain criteria";
var content_f16_53_17243=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F50578&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F50578&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for the diagnosis of psychosomatic recurrent abdominal pain* in children and adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Organic disorders must be excluded, and at least six of the following seven criteria must be satisfied:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. Onset or aggravation of chronic negative stress at the time of onset of recurrent pain",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Pain parallels chronic negative stress",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Better or pain-free during periods of lessened/disappeared chronic negative stress",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Acute stress induces pain",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Most pain attacks related to acute stress",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. The child followed up for at least one year",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Parents/child and responsible doctor agree about the diagnosis",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Recurrent abdominal pain: At least three episodes of recurrent abdominal pain severe enough to affect the child's activities over a period of more than three months.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Alfven G. One hundred cases of recurrent abdominal pain in children: diagnostic procedure and criteria for psychosomatic diagnosis. Acta Paediatr 2003; 92:43.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
