var title_f16_55_17275="Criteria avoidant personality";
var content_f16_55_17275=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F59866&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F59866&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for avoidant personality disorder",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Four or more of the following plus general criteria for personality disorder:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. Avoids occupational activities that involve significant interpersonal contact because of fears of criticism, disapproval, or rejection",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Is unwilling to get involved with people unless certain of being liked",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Shows restraint within intimate relationships because of the fear of being shamed or ridiculed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Is preoccupied with being criticized or rejected in social situations",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       5. Is inhibited in new interpersonal situations because of feelings of inadequacy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       6. Views self as socially inept, personally unappealing, or inferior to others",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       7. Is unusually reluctant to take personal risks or to engage in any new activities because they may prove embarrassing",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from American Psychiatric Association. Diagnostic and Statistical Manual of Mental Disorders, 4th ed, Text revision (DSM-IV-TR). American Psychiatric Association, Washington, DC, 2000.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
