" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Neuroimaging of acute ischemic stroke",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/56/17290/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/56/17290/contributors\">",
"     Jamary Oliveira-Filho, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/56/17290/contributors\">",
"     Walter J Koroshetz, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/56/17290/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/56/17290/contributors\">",
"     Scott E Kasner, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/56/17290/contributors\">",
"     Eric D Schwartz, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?16/56/17290/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?16/56/17290/contributors\">",
"     John F Dashe, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
