var title_f16_56_17293="Fertility preservation for women treated w gonadotoxic therapies";
var content_f16_56_17293=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F65144&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F65144&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Fertility preservation options for women treated with gonadotoxic therapies",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Investigational",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Cryopreservation of embryos for future implantation",
"       </td>",
"       <td>",
"        Typically requires ovarian stimulation, which takes 10 to 14 days from the beginning of a menstrual cycle. Oocytes are harvested under ultrasound guidance and fertilized in vitro.",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        <p>",
"         Requires sperm from the woman's partner. If no partner, then donor sperm can be used.",
"        </p>",
"        <p>",
"         Induction of high estrogen levels during ovarian stimulation is a concern in women with estrogen-responsive malignancies. Aromatase inhibitors can be used to minimize estrogen exposure.",
"        </p>",
"        Costly. Approximately $8000 to 12,000 per cycle, $500 to 1000/year storage fees.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Oocyte cryopreservation (mature oocytes)",
"       </td>",
"       <td>",
"        Typically requires ovarian stimulation, which takes 10 to 14 days from the beginning of a menstrual cycle. Oocytes are harvested under ultrasound guidance and frozen without being fertilized.",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        <p>",
"         Induction of high estrogen levels during ovarian stimulation is a concern in women with estrogen-responsive malignancies. Aromatase inhibitors can be used to minimize estrogen exposure.",
"        </p>",
"        Costly. Approximately $8000 to 12,000 per cycle, $500 to 1000/year storage fees.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Oocyte cryopreservation (immature oocytes)",
"       </td>",
"       <td>",
"        Does not require ovarian stimulation. Germinal oocytes are harvested under ultrasound guidance and frozen without being fertilized.",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        Only a few pregnancies have been reported.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Ovarian cryopreservation and transplantation",
"       </td>",
"       <td>",
"        Outpatient surgical procedure to obtain strips of ovarian tissue, which are frozen and reimplanted when pregnancy is desired.",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        Ovarian transplantation is not recommended in women at increased risk of developing ovarian cancer.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Gonadal shielding during radiation therapy",
"       </td>",
"       <td>",
"        Shielding gonads during radiation therapy reduces gonadal exposure to scatter radiation.",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        <p>",
"         Not possible if gonads are in the radiation field.",
"        </p>",
"        Requires expertise to properly place shields.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Ovarian transposition (oophoropexy)",
"       </td>",
"       <td>",
"        <p>",
"         Outpatient surgical procedure to reposition the ovaries away from the radiation field.",
"        </p>",
"        Should be performed just before radiation therapy to minimize chance that ovaries will return to their former position.",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        <p>",
"         Approximately 50 percent chance of success.",
"        </p>",
"        Ovaries may need to be repositioned to allow assisted or natural conception.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Trachelectomy",
"       </td>",
"       <td>",
"        Inpatient surgical procedure to remove the cervix while preserving the uterus.",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        <p>",
"         Only useful for women with early stage cervical cancer.",
"        </p>",
"        <p>",
"         Prior to attempting pregnancy following trachelectomy, the patient may require an abdominal cerclage.",
"        </p>",
"        Pregnancy following abdominal cerclage may be complicated due to the difficulty in differentiating preterm labor from preterm contractions, the risk of uterine rupture, and the need for cesarean delivery.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ovarian suppression with gonadotropin releasing hormone (GnRH) analogs or antagonists",
"       </td>",
"       <td>",
"        Administer before and concurrent with chemotherapy to protect ovarian tissue.",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        <p>",
"         True efficacy unknown. No randomized trial has shown benefit to fertility.",
"        </p>",
"        <p>",
"         Side effects include hot flashes and vaginal atrophy. Suppression of menses is a benefit in women at risk of menorrhagia from thrombocytopenia.",
"        </p>",
"        Approximately $500/month.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Lee SJ, Schover LR, Patridge AH, et al. American Society of Clinical Oncology recommendations on fertility preservation in cancer patients. J Clin Oncol 2006; 24:2917.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
