" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Effects of testosterone deficiency according to age at onset",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Age",
"       </td>",
"       <td class=\"subtitle1\">",
"        Effects of testosterone deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        First trimester in utero",
"       </td>",
"       <td>",
"        Incomplete virilization of external genitalia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Incomplete development of Wolffian ducts to form male internal genitalia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Third trimester in utero",
"       </td>",
"       <td>",
"        Micropenis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Prepuberty",
"       </td>",
"       <td>",
"        Incomplete pubertal maturation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eunuchoidal body habitus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Poor muscle development and reduced peak bone mass",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Postpuberty",
"       </td>",
"       <td>",
"        Decrease in energy, mood, and libido",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decrease in sexual hair, hematocrit, muscle mass and strength, and bone mineral density",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
