var title_f16_6_16492="Rabies preexposure guide";
var content_f16_6_16492=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F61388&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F61388&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rabies preexposure prophylaxis guide - United States, 2008",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Risk category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Nature of risk",
"       </td>",
"       <td class=\"subtitle1\">",
"        Typical populations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Preexposure recommendations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Continuous",
"       </td>",
"       <td>",
"        Virus present continuously, often in high concentrations. Specific exposures likely to go unrecognized. Bite, nonbite, or aerosol exposure.",
"       </td>",
"       <td>",
"        Rabies research laboratory workers; rabies biologics production workers.",
"       </td>",
"       <td>",
"        Primary course. Serologic testing every&nbsp;six months; booster vaccination if antibody titer is below acceptable level.*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Frequent",
"       </td>",
"       <td>",
"        Exposure usually episodic, with source recognized, but exposure also might be unrecognized. Bite, nonbite, or aerosol exposure.",
"       </td>",
"       <td>",
"        Rabies diagnostic laboratory workers, cavers, veterinarians and staff, and animal-control and wildlife workers in areas where rabies is enzootic. All persons who frequently handle bats.",
"       </td>",
"       <td>",
"        Primary course. Serologic testing every&nbsp;two years; booster vaccination if antibody titer is below acceptable level.*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infrequent (greater than population at large)",
"       </td>",
"       <td>",
"        Exposure nearly always episodic with source recognized. Bite or nonbite exposure.",
"       </td>",
"       <td>",
"        Veterinarians and animal-control staff working with terrestrial animals in areas where rabies is uncommon to rare. Veterinary students. Travelers visiting areas where rabies is enzootic and immediate access to appropriate medical care including biologics is limited.",
"       </td>",
"       <td>",
"        Primary course. No serologic testing or booster vaccination.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rare (population at large)",
"       </td>",
"       <td>",
"        Exposure always episodic with source recognized. Bite or nonbite exposure.",
"       </td>",
"       <td>",
"        US population at large, including persons in areas where rabies is epizootic.",
"       </td>",
"       <td>",
"        No vaccination necessary.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Minimum acceptable antibody level is complete virus neutralization at a 1:5 serum dilution by the rapid fluorescent focus inhibition test. A booster dose should be administered if the titer falls below this level.",
"    </div>",
"    <div class=\"reference\">",
"     Manning SE, Rupprecht CE, Fishbein D, et al. Human rabies prevention-United States, 2008: recommendations of the Advisory Committee on Immunization Practices. MMWR Recomm Rep 2008; 57:1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
