var title_f16_63_17403="Antibiotics for lung abscess";
var content_f16_63_17403=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F64257&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F64257&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antibiotic selections in patients with anaerobic lung abscess",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Preferred",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Clindamycin or beta lactam-beta lactamase inhibitor combination drugs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Drugs with established merit from clinical trials",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Penicillin, tetracycline (data from 1950-1970)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Metronidazole plus penicillin (data from 1980-1990)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Drugs with good in vitro activity against anaerobes but minimal clinical data",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Chloramphenicol, cefoxitin, cefotetan, moxifloxacin, piperacillin, imipenem, meropenem",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Drugs with variable activity against anaerobes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cephalosporins, tetracycline, macrolides, levofloxacin, penicillin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Drugs with virtually no activity against anaerobes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Aztreonam, trimethoprim-sulfamethoxazole, aminoglycosides, ciprofloxacin",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
