var title_f16_9_16539="Signs malnutrition HD patients";
var content_f16_9_16539=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F72365&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F72365&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indices of malnutrition in hemodialysis patients",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Serum albumin concentration less than 4.0 g/dL (40",
"g/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Continuous decline in estimated dry weight",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Protein catabolic rate less than 0.8 g/kg per day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Body weight less than 80 percent of ideal weight",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low serum creatinine and urea concentrations in",
"patients without residual renal function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Marked reduction in anthropometric measurements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cholesterol concentration less than 150 mg/dL (3.9",
"mmol/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Transferrin concentration less than 150 mg/dL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Insulin growth factor-1 concentration less than 300",
"      &micro;g/L",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low",
"predialysis serum potassium (and possibly phosphorous) concentration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low serum prealbumin concentration (less than 30 mg/dL)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
