var title_f16_9_16540="Histological classification of panniculitis";
var content_f16_9_16540=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F85715&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F85715&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of panniculitis based upon histology",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Predominantly septal panniculitis, without vasculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Erythema nodosum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Erythema nodosum migrans/subacute nodular migratory panniculitis/chronic erythema nodosum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Deep morphea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Necrobiosis lipoidica diabeticorum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rheumatoid nodule",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Subcutaneous granuloma annulare",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Predominantly septal panniculitis, with vasculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Polyarteritis nodosa",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Superficial migratory thrombophlebitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Predominantly lobular panniculitis, without vasculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infectious panniculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Traumatic panniculitis (cold, injections, post-irradiation, blunt trauma)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Enzymatic destruction (pancreatic panniculitis, alpha-1 antitrypsin deficiency*)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Malignancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Deposition (gout, calciphylaxis, hyperoxaluria)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lipodermatosclerosis/sclerosing panniculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lupus panniculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Panniculitis of dermatomyositis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Connective tissue disease panniculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Post-steroid panniculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Subcutaneous sarcoidosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Predominantly lobular panniculitis with vasculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Erythema induratum/nodular vasculitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Erythema nodosum leprosum",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Alpha-1 antitrypsin deficiency panniculitis has been variably described as predominantly septal and predominantly lobular.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
