var title_f17_0_17419="Sun protection measures";
var content_f17_0_17419=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F83780&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F83780&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Sun protection measures",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Minimize sun exposure between 10 AM and 4 PM",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wear a wide-brimmed hat (4 inches or larger), long-sleeved shirt, and long pants. Choose tightly woven materials or ultraviolet protection factor (UPF) clothing.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Apply sunscreen when sun exposure is unavoidable. Apply one hour prior to sun exposure to all areas of the skin that are exposed to the sun and reapply frequently (every two hours) and liberally. Use a broad spectrum (UVA and UVB) sunscreen with an SPF of 30 or greater.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Be aware of the sun exposure risk with high-altitude activities including mountain climbing, biking, hiking, or skiing, in locations near the equator, and on cloudy or overcast days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Individuals at high risk for skin cancer, such as outdoor workers, fair-skinned individuals, patients with a history of skin cancer, and patients with atypical mole syndrome, should apply sunscreen daily to exposed skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infants less than six months of age should be kept out of the sun; children six months and older should be protected with clothing and sunscreens",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     <p>",
"      &nbsp;",
"     </p>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
