" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Muscles involved in thumb movement",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Muscle",
"       </td>",
"       <td class=\"subtitle1\">",
"        Function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        EPL - Extensor pollicis longus",
"       </td>",
"       <td>",
"        Extends IP joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        FPL - Flexor pollicis longus",
"       </td>",
"       <td>",
"        Flexes IP joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        EPB - Extensor pollicis brevis",
"       </td>",
"       <td>",
"        Extends MCP joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        FPB - Flexor pollicis brevis",
"       </td>",
"       <td>",
"        Flexes MCP joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         APL - Abductor pollicis longus",
"        </p>",
"        <p>",
"         APB - Abductor pollicis brevis",
"        </p>",
"       </td>",
"       <td>",
"        Abducts thumb metacarpal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        AP - Adductor pollicis",
"       </td>",
"       <td>",
"        Adducts thumb metacarpal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        OP - Opponens pollicis",
"       </td>",
"       <td>",
"        Flexes and abducts thumb metacarpal (pinch with little finger isolates OP)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1st interosseus",
"       </td>",
"       <td>",
"        Works with OP to enable pinch against index finger",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
