var title_f17_10_17581="Aerosol device comparison";
var content_f17_10_17581=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F71527&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F71527&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Advantages and disadvantages of various aerosol devices",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Jet nebulizer*",
"       </td>",
"       <td>",
"        <p>",
"         Patient coordination not required",
"        </p>",
"        <p>",
"         High doses possible",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Generally more expensive than metered dose inhaler",
"        </p>",
"        <p>",
"         More time required",
"        </p>",
"        <p>",
"         Contamination possible",
"        </p>",
"        <p>",
"         Device preparation required before treatment",
"        </p>",
"        <p>",
"         Not all medications available",
"        </p>",
"        <p>",
"         Less efficient than other devices (dead volume loss)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Mesh nebulizer (eg, Aeroneb",
"        <sup>",
"         &reg;",
"        </sup>",
"        , eFlow",
"        <sup>",
"         &reg;",
"        </sup>",
"        , Omron MicroAir",
"        <sup>",
"         &reg;",
"        </sup>",
"        , I-neb)",
"       </td>",
"       <td>",
"        <p>",
"         Patient coordination not required",
"        </p>",
"        <p>",
"         High doses possible",
"        </p>",
"        <p>",
"         Quiet",
"        </p>",
"        <p>",
"         Faster delivery than jet nebulizer",
"        </p>",
"        <p>",
"         More accurate delivery of desired dose than jet nebulizer",
"        </p>",
"        <p>",
"         Portable, battery operated",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Expensive",
"        </p>",
"        <p>",
"         Contamination possible",
"        </p>",
"        <p>",
"         Device preparation required before treatment",
"        </p>",
"        <p>",
"         Cleaning required after dose",
"        </p>",
"        <p>",
"         Not all medications available",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Ultrasonic nebulizer (eg, OPTI-NEB&trade;, Beetle Neb, Lumiscope, MiniBreeze)",
"       </td>",
"       <td>",
"        <p>",
"         Patient coordination not required",
"        </p>",
"        <p>",
"         High doses possible",
"        </p>",
"        <p>",
"         Small dead volume",
"        </p>",
"        <p>",
"         Quiet",
"        </p>",
"        <p>",
"         No drug loss during exhalation",
"        </p>",
"        <p>",
"         Faster delivery than jet nebulizer",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Expensive",
"        </p>",
"        <p>",
"         Contamination possible",
"        </p>",
"        <p>",
"         Prone to malfunction",
"        </p>",
"        <p>",
"         Device preparation required before treatment",
"        </p>",
"        <p>",
"         Cannot use with medications in suspension (eg, budesonide)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Metered-dose inhaler",
"       </td>",
"       <td>",
"        <p>",
"         Convenient",
"        </p>",
"        <p>",
"         Less expensive than nebulizer",
"        </p>",
"        <p>",
"         Portable",
"        </p>",
"        <p>",
"         More efficient than nebulizer",
"        </p>",
"        <p>",
"         No drug preparation required",
"        </p>",
"        <p>",
"         Difficult to contaminate",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Patient coordination essential",
"        </p>",
"        <p>",
"         Patient actuation required",
"        </p>",
"        <p>",
"         Large pharyngeal deposition",
"        </p>",
"        <p>",
"         Difficult to deliver high doses",
"        </p>",
"        <p>",
"         Not all medications available",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Metered-dose inhaler with holding chamber",
"       </td>",
"       <td>",
"        <p>",
"         Less patient coordination required",
"        </p>",
"        <p>",
"         Less pharyngeal deposition",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         More expensive than MDI alone",
"        </p>",
"        <p>",
"         Less portable than MDI alone",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Dry powder inhaler",
"       </td>",
"       <td>",
"        <p>",
"         Less patient coordination required",
"        </p>",
"        <p>",
"         Convenient",
"        </p>",
"        <p>",
"         Propellant not required",
"        </p>",
"        <p>",
"         Portable",
"        </p>",
"        <p>",
"         Breath-actuated",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Requires moderate to high inspiratory flow",
"        </p>",
"        <p>",
"         Some units are single dose",
"        </p>",
"        <p>",
"         Can result in high pharyngeal deposition",
"        </p>",
"        <p>",
"         Not all medications available",
"        </p>",
"        <p>",
"         Difficult to deliver high doses",
"        </p>",
"        <p>",
"         Cannot use with endotracheal or tracheostomy tubes",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CFC: chlorofluorocarbon; MDI: metered-dose inhaler.",
"     <br>",
"      * Many brands are commercially available in both disposable and non-disposable designs for use in the hospital and at home.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Consensus Statement: Aerosols and Delivery Devices. Respir Care 2000; 45:589.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
