"    <li class=\"plainItem\">",
"     <a class=\"medical medical_review\" href=\"UTD.htm?23/22/23913?source=related_link\">",
"      Subclinical hyperthyroidism",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"medical medical_review\" href=\"UTD.htm?3/48/3850?source=related_link\">",
"      Subclinical hypothyroidism",
"     </a>",
"    </li>",
"   </ul>",
"  </div>",
