var title_f17_11_17596="Discussing contraception with adolescents";
var content_f17_11_17596=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F85698&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F85698&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Specific tips for counseling adolescents about contraception",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Counseling",
"       </td>",
"       <td class=\"subtitle1\">",
"        Specific examples",
"       </td>",
"       <td class=\"subtitle1\">",
"        Tips",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Assessing pregnancy intention or ambivalence",
"       </td>",
"       <td>",
"        <p>",
"         \"What are your thoughts on pregnancy or delaying pregnancy?\"",
"        </p>",
"        <p>",
"         \"At present, in the next couple years, and long-term?\"",
"        </p>",
"        <p>",
"         \"If you were to be pregnant, would that be a good thing or a problem for you?\"",
"        </p>",
"        \"Are you interested starting or continuing birth control?\"",
"       </td>",
"       <td>",
"        <p>",
"         Avoid assuming all adolescents want to contracept",
"        </p>",
"        Adherence failures may indicate ambivalence or an unstated desire to become pregnant",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Assessing birth control methods and options",
"       </td>",
"       <td>",
"        <p>",
"         \"What birth control do you use now?\"",
"        </p>",
"        <p>",
"         \"What kind of birth control does your partner use?\"",
"        </p>",
"        <p>",
"         \"What birth control have you tried or used in the past?\"",
"        </p>",
"        <p>",
"         \"What did you like or not like about that birth control method(s)?\"",
"        </p>",
"        \"Was your method(s) easy to do or take?\"",
"       </td>",
"       <td>",
"        <p>",
"         Don't forget to include boys and men in discussions about pregnancy planning and contraception",
"        </p>",
"        <p>",
"         Women who decline birth control or who are condom only users should be offered Plan B and prenatal multivitamins (This includes young women who have sex with women)",
"        </p>",
"        Consider long-acting reversible contraceptives (intrauterine devices and progestin implants) for superior effectiveness and ease of use",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Assessing side effects, concerns, and next steps",
"       </td>",
"       <td>",
"        <p>",
"         \"Did you have any problems keeping up with your method?\"",
"        </p>",
"        <p>",
"         \"Did you have any side effects that made you concerned?\"",
"        </p>",
"        \"If you want to try method X, we can always reassess how this is working for you in Y weeks or months.\"",
"       </td>",
"       <td>",
"        <p>",
"         Thorough counseling and education about normal and abnormal side-effects up front may help patients continue on their method",
"        </p>",
"        Encourage patients to call or return to clinic with questions or concerns, rather than just discontinuing a method",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
