var title_f17_11_17597="Imaging guidelines for newly diagnosed osteosarcoma";
var content_f17_11_17597=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F55623&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F55623&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for imaging of osteosarcoma from the Children's Oncology Group (all imaging studies are required unless otherwise noted)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        At presentation and prior to surgery/local control",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Site",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Primary and bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Primary tumor",
"       </td>",
"       <td class=\"indent1\">",
"        MRI with gadolinium (presurgery exam should be performed within four weeks of surgery)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with contrast (recommended for possible bone metastases if diagnosis uncertain)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chest",
"       </td>",
"       <td class=\"indent1\">",
"        CT; AP and lateral radiographs (recommended at presentation only)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Whole body",
"       </td>",
"       <td class=\"indent1\">",
"        Technetium bone scintigraphy (with SPECT recommended) and/or FDG-PET",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Baseline after surgery/local control",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Site",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Testing and timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Primary and bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs within two weeks of surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Primary tumor",
"       </td>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with IV contrast (in patients without significant metallic artifact at primary tumor site) three or four months after local control",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with contrast (recommended for possible bone metastases if diagnosis uncertain)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Surveillance on chemotherapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Site",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Testing and timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Primary and bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs after three cycles (approximately halfway through postoperative chemotherapy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with IV contrast recommended if symptoms or abnormal imaging (and surgical or other intervention contemplated)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chest",
"       </td>",
"       <td class=\"indent1\">",
"        CT after four cycles (approximately halfway through postoperative chemotherapy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Whole body",
"       </td>",
"       <td class=\"indent1\">",
"        Technetium bone scintigraphy (add SPECT of lungs if pulmonary metastases suspected) or FDG-PET (if disease positive on prior FDG-PET) at the end of cytotoxic chemotherapy, sooner if symptoms or abnormal imaging (and surgical or other intervention contemplated)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"2\">",
"        Surveillance postchemotherapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Site",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        Testing and timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Primary and bone metastases",
"       </td>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs every three months x 8, then every six months x 6, then every 12 months x 5",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        MRI with gadolinium or CT scan with IV contrast recommended if symptoms or abnormal imaging (and surgical or other intervention contemplated)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Chest",
"       </td>",
"       <td class=\"indent1\">",
"        CT every three months x 8, then every six months x 2, then every 12 months x 2",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        AP and lateral radiographs every 12 months x 5 (starting after last scheduled chest CT)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Whole body",
"       </td>",
"       <td class=\"indent1\">",
"        Technetium bone scintigraphy or FDG-PET (if disease positive on prior FDG-PET) if symptoms or abnormal imaging (and surgical or other intervention contemplated)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AP: anterior-posterior; CT: computed tomography scan; FDG-PET: fluorodeoxyglucose positron emission tomography; MRI: magnetic resonance imaging; SPECT: single-photon emission computed tomography.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Meyer JS, Nadel HR, Marina N, et al. Pediatr Blood Cancer 2008; 51:63.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
