var title_f17_14_17644="Nomenclature and classification for neuroendocrine tumors";
var content_f17_14_17644=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F60365&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F60365&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ENETS/WHO Nomenclature and classification for digestive system neuroendocrine tumors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Differentiation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mitotic count*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Ki-67 index",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td class=\"subtitle1\">",
"        Traditional",
"       </td>",
"       <td class=\"subtitle1\">",
"        ENETS, WHO",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Well differentiated",
"       </td>",
"       <td>",
"        Low grade (G1)",
"       </td>",
"       <td>",
"        &lt;2 per 10 HPF",
"       </td>",
"       <td>",
"        &lt;3 percent",
"       </td>",
"       <td>",
"        Carcinoid, islet cell, pancreatic (neuro)endocrine tumor",
"       </td>",
"       <td>",
"        Neuroendocrine tumor, Grade 1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intermediate grade (G2)",
"       </td>",
"       <td>",
"        2-20 per 10 HPF",
"       </td>",
"       <td>",
"        3-20 percent",
"       </td>",
"       <td>",
"        Carcinoid, atypical carcinoid",
"        <sup>",
"         &Delta;",
"        </sup>",
"        , islet cell, pancreatic (neuro)endocrine tumor",
"       </td>",
"       <td>",
"        Neuroendocrine tumor, Grade 2",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Poorly differentiated",
"       </td>",
"       <td rowspan=\"2\">",
"        High grade (G3)",
"       </td>",
"       <td rowspan=\"2\">",
"        &gt;20 per 10 HPF",
"       </td>",
"       <td rowspan=\"2\">",
"        &gt;20 percent",
"       </td>",
"       <td>",
"        Small cell carcinoma",
"       </td>",
"       <td>",
"        Neuroendocrine carcinoma, Grade 3, small cell",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Large cell neuroendocrine carcinoma",
"       </td>",
"       <td>",
"        Neuroendocrine carcinoma, Grade 3, large cell",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ENETS: European Neuroendocrine Tumor Society; WHO: World Health Organization.",
"     <br>",
"      * Counted in 10 high power fields (HPF). 10 HPF = 2 mm",
"      <sup>",
"       2",
"      </sup>",
"      , at least 40 fields (at 400x magnification) evaluated in areas of highest mitotic density. Cut-offs per American Joint Commission on Cancer Staging Manual, 7th edition.",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       Ki-67 index as assessed by MIB1 antibody staining: percent positive after count of 2000 cells in area of highest nuclear labeling. Cut-offs per American Joint Commission on Cancer Staging Manual, 7th edition.",
"       <br>",
"        &Delta; The term \"atypical carcinoid\" only applies to intermediate grade NETs of the lung.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
