var title_f17_15_17659="Findings that increase the certainty of dx of preeclampsia";
var content_f17_15_17659=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F60502&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=13\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F60502&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=13\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Findings which increase the certainty of the diagnosis of preeclampsia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Systolic blood pressure of 160 mm Hg or more",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diastolic blood pressure of 110 mm Hg or more",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Proteinuria occurring for the first time during pregnancy, especially if 2.0 g or more in 24 hours. A qualitative result of 2+ or 3+ is also suggestive.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum creatinine greater than 1.2 mg/dL (106 mmol/L)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Platelet count less than 100,000 cells per cubic millimeter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Evidence of microangiopathic hemolytic anemia (eg, elevated lactic acid dehydrogenase)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Elevated liver enzymes (eg, alanine aminotransferase or aspartate aminotransferase)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent headache or other cerebral or visual disturbances",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent epigastric pain",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Working group report on high blood pressure in pregnancy. National Instititutes of Health, Washington, DC 2000.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
